        console.log(e.target);
        console.log(dragItem.current);
        console.log("draged card in column: "+statusId);
        let statuses = table.statuses;
    };

    return (
        <div className={"scrumTable"}>
            {statuses.map(status => {
                return <Column dragItem ={dragItem}
