import React, {useContext} from 'react';
import {useHistory} from "react-router-dom";
import {DeleteOutlined} from "@ant-design/icons";
import {ContentContainer} from "../styledComps/styled";
import {ProjectContext} from "../contexts/ProjectContext";
import axios from "axios";

const ProjectCard = ({project, taskPercentageInProjectStatuses}) => {

    const history = useHistory();

    const {getProjects} = useContext(ProjectContext);

    const handleClick = () => {
        history.push("/project/" + project.id)
    };

    const startEndColor=taskPercentageInProjectStatuses.start===0?0:taskPercentageInProjectStatuses.start-8;

    const inProgressStartColor = taskPercentageInProjectStatuses.inProgress===0?0:taskPercentageInProjectStatuses.start;
    const inProgressEndColorPercentage = taskPercentageInProjectStatuses.inProgress?0:taskPercentageInProjectStatuses.start + taskPercentageInProjectStatuses.inProgress-8;

    const finalEndColorPercentage = inProgressEndColorPercentage+taskPercentageInProjectStatuses.finish;

    const ProjectBackground = {

        background: `linear-gradient(90deg, #706B74 0% ${startEndColor}% ,
         rgba(253,192,29,1) ${inProgressStartColor}%  ${inProgressEndColorPercentage}% ,
         rgba(69,252,70,1) ${inProgressEndColorPercentage}%  ${finalEndColorPercentage}% )`
    };

    const handleDelete = async (e) => {
        e.stopPropagation();
        await axios.delete("http://localhost:8080/project/delete/" + project.id);
        getProjects();
    };

    return (
        <div className={"project_card"} onClick={handleClick} style={ProjectBackground}>
            <div className={"status_tool_container"}><DeleteOutlined onClick={(e) => handleDelete(e)}/></div>
            <ContentContainer><h2>{project.title}</h2></ContentContainer>
        </div>
    );
};

export default ProjectCard;
