import React, {useContext, useEffect, useState} from "react";
import {ProjectContext} from "../contexts/ProjectContext";
import AddNewProject from "./AddNewProject";
import ProjectCard from "./ProjectCard";
import {ContentContainer} from "../styledComps/styled"

const ProjectList = () => {

    const {getProjects, projects} = useContext(ProjectContext);

    useEffect(() => {
            getProjects();
        }, []
    );

    function taskCardDistribution(project) {
        let projectStatuses = project.table.statuses;
        let taskCounts = {start:0,inProgress:0,finish:0};
        projectStatuses.map((status,i)=>{
            if (i===0){
                taskCounts[start]+=status.length;
                console.log(taskCounts[start])
            }
            if (i===projectStatuses.length-1){
                taskCounts.finish+=status.length;
            }else {
                taskCounts.inProgress+=status.length;
            }
        });
        console.log(taskCounts);
        return taskCounts;
    }

    return (
        <div className="project_page">
            <ContentContainer><h2>Your Projects</h2></ContentContainer>
            <ContentContainer className="project_page__project_list_container">
                {projects.length === 0 ?
                    <div>You don't have any project yet.</div>
                    :
                    projects.map((project) => {
                        let statusNums = taskCardDistribution(project);
                        return <ProjectCard key={project.id} project={project}/>}
                    )}
            </ContentContainer>
            <AddNewProject/>
        </div>
    )
};

export default ProjectList;
