import React, {useRef, useState} from 'react';
import { Modal, Button } from 'antd';
import 'antd/dist/antd.css';
import { FormOutlined } from "@ant-design/icons";
import {Input} from "../styledComps/styled";


const TaskModal = ({task, setTask}) => {

    const [visible,setVisible] = useState(false);

    const BusinessValueRef= useRef(task.businessValue);


    function handleOk() {
        console.log("halli")
        console.log(BusinessValueRef.current.value)
    }

    function handleCancel() {
        console.log(task);
        setVisible(false);
    }

    function showModal() {
        setVisible(true)
    }

    return (
        <div>
            < FormOutlined onClick={showModal}/>
            <Modal
                visible={visible}
                onOk={handleOk}
                onCancel={handleCancel}
            >
                <h2>Edit Task</h2>
                <div>
                <label>Business value: </label>
                <Input placeholder={"Business Value"}
                       defaultValue={task.businessValue}
                       ref={BusinessValueRef}/>
                </div>
                <div>
                <label>Description: </label>
                <Input placeholder={"Description"}
                       defaultValue={task.description}
                       ref={BusinessValueRef}/>
                </div>
                <div>
                <label>Title: </label>
                <Input placeholder={"Title"}
                       defaultValue={task.title}
                       ref={BusinessValueRef}/>
                </div>
                       <div>
                <label>Position: </label>
                <Input placeholder={"Position"}
                       defaultValue={task.position}
                       ref={BusinessValueRef}/>
                       </div>

            </Modal>


        </div>
    );
};

export default TaskModal;
