import React, {useEffect, useState} from 'react';
import {SnippetsOutlined} from "@ant-design/icons";
import UserCalls from "../Services/UserCalls";
import {use} from "@js-joda/core";

const HomePage = () => {

    let [user, setUser] = useState({});

    const getUser = async () => {
        let userObj = await UserCalls.getUser(localStorage.getItem("username"));
        console.log(userObj);
        setUser(userObj.data);
    };

    useEffect(() => {
        getUser();
    }, []);

    return (
        <div className={"homepage_container"}>
            <div className="homepage">
                <div className={"homepage_data_container"}>
                    <h1>WELCOME {localStorage.getItem("username")} !</h1>
                    <a href={"/projects"}>Let's check your projects <SnippetsOutlined/> </a>
                </div>
                <div>
                <div className={"homepage_data_container"}>
                    <SnippetsOutlined/>
                    <p> Total projects.</p>
                    <p>{user.projectsCount} </p>
                </div>
                <div className={"homepage_data_container"}>
                    <SnippetsOutlined/>
                    <p> Participated</p>
                    <p>{user.participantCount}</p>
                </div>
                <div className={"homepage_data_container"}>
                    <SnippetsOutlined/>
                    <p> Total Tasks</p>
                    <p>{user.tasksCount}  </p>
                </div>
                </div>
            </div>
        </div>
    );
};

export default HomePage;
