import React, {useState, useRef} from 'react';

const Task = ({task, statusId}) => {

    const dragItem = useRef();
    const [dragging, setDragging] = useState(false);

    const handleDrag = (e) => {
            setDragging(true);
            let dragItemParams = {taskId: task.id, statusId: statusId}
            dragItem.current = dragItemParams;
            console.log(dragItem);
        };

    const draggingStyle= (currTaskId)=>{
        if (currTaskId===dragItem.current.taskId){
            return "dragged task_card"
        }
        return "task_card";
    };



    return (
        <div className={dragging? draggingStyle(task.id) :"task_card"} draggable={true} onDragStart={(event) => (handleDrag(event))}>
            {task.title}
        </div>
    );
};

export default Task;
