import React, {useRef} from 'react';
import Task from "./Task";

const Column = ({status, onDragEnter}) => {


    return (
        <div className={"project_culomn"}>
            {status.statusName}
            {status.tasks.length === 0 ?
                <h3>no task yet</h3>
                : status.tasks.map(task => {
                return <Task
                             statusId = {status.id}
                             key={task.id}
                             task={task}
                             onDragEnter={onDragEnter}
                />
            })}
        </div>
    );
};

export default Column;
