import React, {useState, useContext, useEffect} from 'react';
import {useParams} from "react-router";
import axios from "axios";

const ProjectPage = () => {

    const {id} = useParams();
    const [project, setProject] = useState();
    const [states, setStates] = useState();
    const [loading, setLoading] = useState(true);

    const getProjects = async () => {
        let response = await axios.get("http://localhost:8080/project/" + id);
        setProject(response.data);
        setStates(response.data.table.statuses);
        console.log(response.data);
        console.log(response.data.table.statuses);
        setLoading(false);
    };

    useEffect(() => {
        getProjects();
    }, []);

    return (
        <div>
            {loading ?
                <h1>loading....</h1>
                :
                <div className={"projectContainer"}>
                    <h1>{project.title}</h1>
                    <div className={"statusContainer"}>
                        {states.map(projectStatus =>(
                            <ProjectStatusCulom projectStatus = {projectStatus}/>
                        ))}
                    </div>
                </div>

            }
        </div>
    );
};

export default ProjectPage;
