import React, {useState, useContext, useEffect} from 'react';
import {useParams} from "react-router";
import axios from "axios";
import ProjectStatusCulomn from "./ProjectStatusCulomn";

const ProjectPage = () => {

    const {id} = useParams();
    const [project, setProject] = useState();
    const [states, setStates] = useState();
    const [loading, setLoading] = useState(true);

    const getProject = async () => {
        let response = await axios.get("http://localhost:8080/project/" + id);
        setProject(response.data);
        setStates(response.data.table.statuses);
        console.log(response.data);
        console.log(response.data.table.statuses);
        setLoading(false);
    };

    const addNewColumn = async (columnName,projectId) =>{
        let newStatus = {columnName:columnName, projectId:projectId};
        let axiosResponse = await axios.post("http://localhost:8080/project/newstatus",newStatus);
        setStates(axiosResponse.data.statuses);
    };

    useEffect(() => {
        getProject();
    }, []);

    return (
        <div>
            {loading ?
                <h1>loading....</h1>
                :
                <div className={"projectContainer"}>
                    <h1>{project.title}</h1>
                    <div className={"statusContainer"}>
                        {states.map(projectStatus => (
                            <ProjectStatusCulomn projectStatus={projectStatus}/>
                        ))}
                    </div>
                </div>
            }
            <div>
                <input/>
            </div>
        </div>
    );
};

export default ProjectPage;
