import React, {useState, useRef} from 'react';

const Task = ({task, statusId}) => {

    const dragItem = useRef();
    const [dragging, setDragging] = useState(false);

    const handleDrag = (e) => {
            let dragItemParams = {taskId: task.id, statusId: statusId}
            dragItem.current = dragItemParams;
            console.log(dragItem);
        };



    return (
        <div className={draging? dragingStyle(task.id) :"task_card"} draggable={true} onDragStart={(event) => (handleDrag(event))}>
            {task.title}
        </div>
    );
};

export default Task;
