import React, {useContext} from 'react';
import {useHistory} from "react-router-dom";
import {DeleteOutlined} from "@ant-design/icons";
import {ContentContainer} from "../styledComps/styled";
import {ProjectContext} from "../contexts/ProjectContext";
import axios from "axios";

const ProjectCard = ({project, taskPercentageInProjectStatuses}) => {

    const history = useHistory();

    const {getProjects} = useContext(ProjectContext);

    const handleClick = () => {
        history.push("/project/" + project.id)
    };

    const inProgrsEndColorPersentige =taskPercentageInProjectStatuses.start + taskPercentageInProjectStatuses.inProgress;
    const finalEndColorPersentige = inProgrsEndColorPersentige+taskPercentageInProjectStatuses.finish;

    const ProjectBackgroud = {
        background: `linear-gradient(90deg, #706B74 0% ${taskPercentageInProjectStatuses.start===0?0:taskPercentageInProjectStatuses.start-8}% ,
         rgba(253,192,29,1) ${taskPercentageInProjectStatuses.start}%  ${inProgrsEndColorPersentige===0?0:inProgrsEndColorPersentige-8}% ,
         rgba(69,252,70,1) ${inProgrsEndColorPersentige}%  ${finalEndColorPersentige}% )`
    };

    const handleDelete = async (e) => {
        e.stopPropagation();
        await axios.delete("http://localhost:8080/project/delete/" + project.id);
        getProjects();
    };

    return (
        <div className={"project_card"} onClick={handleClick} style={ProjectBackgroud}>
            <div className={"status_tool_container"}><DeleteOutlined onClick={(e) => handleDelete(e)}/></div>
            <ContentContainer><h2>{project.title}</h2></ContentContainer>
        </div>
    );
};

export default ProjectCard;
