import React, {useState, useRef} from 'react';

const Task = ({task, statusId}) => {

    const dragItem = useRef();

    const
    handleDrag = (e) => {
        console.log(statusId);
        console.log(e);
        let dragItem = {taskId:task.id,statusId:statusId}
        dragItem.current=dragItem;
    };

    return (
        <div className={"task_card"} draggable={true} onDragStart={(event) => (handleDrag(event))}>
            {task.title}
        </div>
    );
};

export default Task;
